import React, { useState, useEffect } from "react";
import axios from "axios";

function App() {
  const [inputText, setInputText] = useState('');
  const [prediction, setPrediction] = useState('');
  const [lrModelProba, setLrModelProba] = useState([]);
  const [data, setData] = useState([]);

  const handleInputChange = (event) => {
    setInputText(event.target.value);
  };

  const handleSubmit = () => {
    // FastAPI'ye POST isteği gönderme
    axios.post('http://localhost:8000/predict_sentiment/', { text: inputText })
      .then((response) => {
        const { prediction, lr_model_proba } = response.data;
        setPrediction(prediction);
        setLrModelProba(lr_model_proba);
        saveToDatabase(inputText, prediction);
      })
      .catch((error) => {
        console.error('Error while making the prediction request:', error);
      });
  };

  const categorizeResults = () => {
    // ... (Önceki kodlar) ...
  };

  // Yeni eklenen fonksiyonlar
  const saveToDatabase = (text, prediction) => {
    axios.post('http://localhost:8000/save_sentiment/', { text, prediction })
      .then((response) => {
        console.log('Veri başarıyla veritabanına kaydedildi.');
        fetchData();
      })
      .catch((error) => {
        console.error('Veritabanına kaydedilirken hata oluştu:', error);
      });
  };

  const fetchData = () => {
    axios.get('http://localhost:8000/get_sentiment_data/')
      .then((response) => {
        setData(response.data.data);
      })
      .catch((error) => {
        console.error('Verileri alma sırasında hata oluştu:', error);
      });
  };

  useEffect(() => {
    fetchData();
  }, []);

  return (
    <>
      <div className="container">
        <h1 className="article">Sentiment Analyzer</h1>
        <div className="leftRight">
          <div className="leftSide">
            <h1 className="smallArticle">Query</h1>
            <textarea
              placeholder="Enter Text"
              rows={9}
              cols={10}
              maxLength={500}
              onChange={handleInputChange}
            ></textarea>
            <button onClick={handleSubmit} className="analizButton">
              Start Analysis
            </button>
          </div>
          <div className="rightSide">
            <h1 className="smallArticle">Results</h1>
            <div className="resultsHeader">
              {/* ... (Sonuçları ekrana yazdır) ... */}
            </div>
          </div>
        </div>
        <div className="dataList">
          <h2>Veritabanı Verileri:</h2>
          <ul>
            {data.map((item) => (
              <li key={item[0]}>
                <span>{item[1]}</span> <span>({item[2]})</span>
              </li>
            ))}
          </ul>
        </div>
        <div className="copyright">
          &copy; {new Date().getFullYear()} All right reserved by {"   "}
          <a href="/">
            <strong>Yağız Ergil & Muhammed Aslan</strong>
          </a>
        </div>
      </div>
    </>
  );
}

export default App;
