import React, { useState } from "react";
import axios from "axios";

function App() {
  const [inputText, setInputText] = useState('');
  const [prediction, setPrediction] = useState('');
  const [lrModelProba, setLrModelProba] = useState([]);

  const handleInputChange = (event) => {
    setInputText(event.target.value);
  };
  const handleSubmit = () => {



  // FastAPI'ye POST isteƒüi g√∂nderme
  axios.post('http://localhost:8000/predict_sentiment/', { text: inputText })
    .then((response) => {
      const { prediction, lr_model_proba } = response.data;
      setPrediction(prediction);
      setLrModelProba(lr_model_proba);
    })
    .catch((error) => {
      console.error('Error while making the prediction request:', error);
    });
};

  // Sonu√ßlarƒ± doƒüru kategoriye yerle≈ütirme fonksiyonu
  const categorizeResults = () => {
    if (lrModelProba.length === 3) {
      const positiveProb = lrModelProba[2] * 100;
      const negativeProb = lrModelProba[0] * 100;
      const neutralProb = lrModelProba[1] * 100;
      const maxProb = Math.max(positiveProb, negativeProb, neutralProb);

      if (maxProb === positiveProb) {
        return [
          { label: "Pozitif", prob: positiveProb },
          { label: "Negatif", prob: negativeProb },
          { label: "N√∂tr", prob: neutralProb },
        ];
      } else if (maxProb === negativeProb) {
        return [
          { label: "Negatif", prob: negativeProb },
          { label: "Pozitif", prob: positiveProb },
          { label: "N√∂tr", prob: neutralProb },
        ];
      } else {
        return [
          { label: "N√∂tr", prob: neutralProb },
          { label: "Pozitif", prob: positiveProb },
          { label: "Negatif", prob: negativeProb },
        ];
      }
    }

    return [];
  };

  return (
    <>
      <div className="container">
        <h1 className="article">Sentiment Analyzer</h1>
        <div className="leftRight">
          <div className="leftSide">
            <h1 className="smallArticle">Query</h1>
            <textarea
              placeholder="Enter Text"
              rows={9}
              cols={10}
              maxLength={500}
              onChange={handleInputChange}
            ></textarea>
            <button onClick={handleSubmit} className="analizButton">
              Start Analysis
            </button>
          </div>
          <div className="rightSide">
            <h1 className="smallArticle">Results</h1>
            <div className="resultsHeader">
              <div style={{ fontWeight: "bold", borderBottom: "3px solid #fff" }}>
                <h4>TAG</h4>
                <span>CONFIDENCE</span>
              </div>
              {/* Burada sonu√ßlarƒ± ekrana yazdƒ±r */}
              {categorizeResults().map(({ label, prob }) => (
                <div key={label}>
                  <h4>
                    <span>{label === "Pozitif" ? "üôÇ" : label === "Negatif" ? "üôÅ" : "üò∂"}</span>
                    {label}
                  </h4>
                  <span>{`${prob.toFixed(2)}%`}</span>
                </div>
              ))}
            </div>
          </div>
        </div>
        <div className="copyright">
            &copy; {new Date().getFullYear()} All right reserved by {"   "}
            <a href="/">
              <strong>Yaƒüƒ±z Ergil & Muhammed Aslan</strong>
            </a>
          </div>
      </div>
    </>
  );
}

export default App;
