import React, { useState, useEffect } from "react";
import axios from "axios";

function App() {
  const [inputText, setInputText] = useState('');
  const [prediction, setPrediction] = useState('');
  const [lrModelProba, setLrModelProba] = useState([]);

  const handleInputChange = (event) => {
    setInputText(event.target.value);
  };

  const handleSubmit = () => {
    // FastAPI'ye POST isteƒüi g√∂nderme
    axios.post('http://localhost:8000/predict_sentiment/', { text: inputText })
      .then((response) => {
        const { prediction, lr_model_proba } = response.data;
        setPrediction(prediction);
        setLrModelProba(lr_model_proba);
      })
      .catch((error) => {
        console.error('Error while making the prediction request:', error);
      });
  };

  useEffect(() => {
    // Sonu√ßlar deƒüi≈ütiƒüinde sƒ±ralamayƒ± kontrol et
    sortResults();
  }, [lrModelProba]);

  const sortResults = () => {
    // Sonu√ßlarƒ± sƒ±ralamak i√ßin y√ºksek y√ºzdeye g√∂re indeksleri bulma
    const positiveIndex = lrModelProba.indexOf(Math.max(...lrModelProba));
    const negativeIndex = lrModelProba.indexOf(Math.min(...lrModelProba));

    // Sonu√ßlarƒ± doƒüru sƒ±raya g√∂re yeniden d√ºzenleme
    const sortedProba = [lrModelProba[positiveIndex], lrModelProba[negativeIndex]];
    setLrModelProba(sortedProba);
  };

  return (
    <>
      <div className="container">
        <h1 className="article">Sentiment Analyzer</h1>
        <div className="leftRight">
          <div className="leftSide">
            <h1 className="smallArticle">Query</h1>
            <textarea
              placeholder="Enter Text"
              rows={9}
              cols={10}
              maxLength={500}
              onChange={handleInputChange}
            ></textarea>
            <button onClick={handleSubmit} className="analizButton">
              Start Analysis
            </button>
          </div>
          <div className="rightSide">
            <h1 className="smallArticle">Results</h1>
            <div className="resultsHeader">
              <div style={{ fontWeight: "bold", borderBottom: "3px solid #fff" }}>
                <h4>TAG</h4>
                <span>CONFIDENCE</span>
              </div>
              <div>
                {prediction.includes("positive") ? ( // Eƒüer pozitifse √∂ne √ßƒ±kar
                  <>
                    <h4>
                      <span>üôÇ</span>Pozitif
                    </h4>
                    <span>%{lrModelProba[0]}</span>
                  </>
                ) : (
                  <>
                    <h4>
                      <span>üôÅ</span>Negatif
                    </h4>
                    <span>%{lrModelProba[1]}</span>
                  </>
                )}
                <div>
                  <h4>
                    <span>üò∂</span>Neutral
                  </h4>
                  <span>%{lrModelProba[2]}</span>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </>
  );
}

export default App;
